import { Component, OnInit } from '@angular/core';
import { FormControl } from '@angular/forms';
import { debounceTime, filter } from 'rxjs';

import { SearchService } from '../../core/services/search.service';
import { SearchBook } from 'src/app/core/models/search-book-response.model';

@Component({
  selector: 'front-end-internship-assignment-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss'],
})
export class HomeComponent implements OnInit {

  private cache: { [key: string]: SearchBook[] } = {};

  bookSearch: FormControl;
  searchResultBooks: SearchBook[] = [];

  currentPage: number = 1; // Current page
  totalPages: number = 1; // Total number of pages

  totalEntries: number = 0; // Total number of entries in the search results


  pageSize: number = 10; // Number of items per page
  pagesArray: number[] = []; // Define and initialize pagesArray as an array of numbers

  isLoading: boolean = false; // Add isLoading variable to track loading state

  constructor(private searchService: SearchService) {
    this.bookSearch = new FormControl('');
  }


  trendingSubjects: Array<any> = [
    { name: 'JavaScript' },
    { name: 'CSS' },
    { name: 'HTML' },
    { name: 'Harry Potter' },
    { name: 'Crypto' },
  ];


  calculateRange(): string {
    const startIndex = (this.currentPage - 1) * this.pageSize + 1;
    const endIndex = Math.min(this.currentPage * this.pageSize, this.totalEntries);
    return `${startIndex} to ${endIndex} of ${this.totalEntries}`;
  }
  ngOnInit(): void {
   
    this.bookSearch.valueChanges.pipe(debounceTime(300)).subscribe((value: string) => {
        this.searchBooks(value, this.pageSize, this.currentPage);
    });
    this.searchBooks('', this.pageSize, this.currentPage);
}


  changePageSize(action: string) {
    if (action === 'up' && this.pageSize < 10) {
      this.pageSize += 1;
    } else if (action === 'down' && this.pageSize > 1) {
      this.pageSize -= 1;
    }
    this.searchBooks(this.bookSearch.value, this.pageSize, this.currentPage);
  }
  
  searchBooks(query: string, limit: number, page: number = 1) {
    this.isLoading = true;

    const offset = (page - 1) * this.pageSize;

    query = this.bookSearch.value ? this.bookSearch.value : null;

    const cacheKey = `${query}_${limit}_${offset}`;
    if (this.cache[cacheKey]) {
      this.searchResultBooks = this.cache[cacheKey];
      this.isLoading = false;
    } else {
      this.searchService.searchBooks(query, limit, offset).subscribe((data) => {
        this.searchResultBooks = data?.docs;
        this.totalEntries = data?.numFound;
        this.currentPage = page;
        this.totalPages = Math.ceil(data?.numFound / this.pageSize);
        this.pagesArray = Array.from({ length: Math.min(5, this.totalPages) }, (_, i) => i + 1);
        this.isLoading = false;
        this.cache[cacheKey] = this.searchResultBooks;
      });
    }
  }

clearSearch() {
  this.bookSearch.setValue('');
}

  goToPreviousPage() {
    console.log('goToPreviousPage');
    console.log(this.currentPage);
    if (this.currentPage >= 1) {
      this.searchBooks(this.bookSearch.value, this.pageSize, this.currentPage - 1);
    }
  }

  goToNextPage() {
    console.log('goToNextPage');
    console.log(this.currentPage);
    if (this.currentPage < this.totalPages) {
      this.searchBooks(this.bookSearch.value, this.pageSize, this.currentPage + 1);
    }
  }

  goToPage(page: number) {
    if (page > 0 && page <= this.totalPages) {
      this.searchBooks(this.bookSearch.value, this.pageSize, page);
    }
  }
}
<div class="header">
  <div class="tw-text-24-px tw-font-600" style="color: #34b27a">
    Open Books Library
  </div>

  <div class="search-container">
    <div class="search-input">
      <mat-icon class="search-icon" aria-label="Search Icon">search</mat-icon>
      <input
        [formControl]="bookSearch"
        name="searchKey"
        type="text"
        placeholder="Search Books"
        class="search-input-field"
      />
      <button
        mat-icon-button
        (click)="clearSearch()"
        class="clear-search-button"
      >
        <mat-icon class="clear-search-icon">clear</mat-icon>
      </button>
    </div>
  </div>
</div>
<div class="tw-flex tw-flex-row tw-mt-24-px tw-ml-24-px">
  <section class="trending-subjects">
    <section class="home-section">
      <div class="home-section-text">Home</div>
    </section>
    <div class="trending-subjects-text">Trending Subjects</div>
    <ul class="trending-subjects-list">
      <li
        class="trending-subjects-item"
        *ngFor="let subject of trendingSubjects"
        routerLink="/trending-subject/{{ subject.name }}"
      >
        {{ subject.name }}
      </li>
    </ul>
  </section>

  <section class="search-results tw-ml-24-px">
    <div class="tw-font-600 tw-text-24-px">Search Results</div>
    <div class="tw-flex tw-items-center tw-mt-4-px">
      <div class="tw-mr-8-px">All ({{ totalEntries }} entries)</div>
    </div>
    <table
      class="tw-table tw-table-fixed tw-w-full tw-mt-16-px tw-border-collapse"
    >
      <thead>
        <tr>
          <th
            class="tw-text-16-px tw-font-600 tw-p-12-px tw-border tw-border-gray-500"
          >
            Title
          </th>
          <th
            class="tw-text-16-px tw-font-600 tw-p-12-px tw-border tw-border-gray-500"
          >
            Author
          </th>
          <th
            class="tw-text-16-px tw-font-600 tw-p-12-px tw-border tw-border-gray-500"
          >
            Year of Publication
          </th>
        </tr>
      </thead>
      <tbody>
        <tr
          *ngFor="let book of searchResultBooks; let even = even"
          [ngClass]="{ 'tw-bg-gray-100': even }"
        >
          <td
            *ngIf="!isLoading && searchResultBooks.length > 0"
            class="tw-text-16-px tw-p-12-px tw-border tw-border-gray-500"
          >
            <div style="color: #34b27a">{{ book.title }}</div>
          </td>
          <td
            *ngIf="!isLoading && searchResultBooks.length > 0"
            class="tw-text-16-px tw-p-12-px tw-border tw-border-gray-500"
          >
            {{ book.author_name }}
          </td>
          <td
            *ngIf="!isLoading && searchResultBooks.length > 0"
            class="tw-text-16-px tw-p-12-px tw-border tw-border-gray-500"
          >
            {{ book.publish_year }}
          </td>
          <td
            *ngIf="!isLoading && searchResultBooks.length === 0"
            colspan="3"
            class="tw-text-16-px tw-p-12-px tw-border tw-border-gray-500"
          >
            No results found
          </td>
        </tr>
        <!-- Add the following SCSS classes to your HTML element -->
        <tr *ngIf="isLoading">
          <td
            colspan="3"
            class="tw-text-16-px tw-p-12-px tw-border tw-border-gray-500"
          >
            <div class="loader"></div>
          </td>
        </tr>
      </tbody>
    </table>

    <div class="pagination-container tw-mt-16-px tw-flex tw-justify-between">
      <div class="tw-ml-16-px tw-self-end tw-text-gray-600">
        Showing {{ calculateRange() }}
      </div>

      <div class="tw-flex tw-items-center tw-space-x-4 tw-mr-16-px">
        <div class="tw-font-600">Display:</div>
        <button
          (click)="changePageSize('up')"
          [disabled]="pageSize === 10"
          class="tw-px-3 tw-py-2 tw-rounded-md tw-text-white hover:tw-bg-blue-600"
        >
          +
        </button>
        <div class="tw-px-3 tw-py-2 tw-rounded-md tw-bg-gray-100">
          {{ pageSize }}
        </div>
        <button
          (click)="changePageSize('down')"
          [disabled]="pageSize === 1"
          class="tw-px-3 tw-py-2 tw-rounded-md tw-text-white hover:tw-bg-blue-600"
        >
          -
        </button>
      </div>

      <div class="tw-flex tw-items-center tw-space-x-4">
        <button
          (click)="goToPreviousPage()"
          [disabled]="currentPage === 1"
          class="tw-px-4 tw-py-2 tw-rounded-md tw-text-white hover:tw-bg-blue-600"
        >
          Previous
        </button>
        <ng-container *ngFor="let page of pagesArray">
          <button
            (click)="goToPage(page)"
            class="tw-px-3 tw-py-2 tw-rounded-md tw-text-white hover:tw-bg-blue-600"
          >
            {{ page }}
          </button>
        </ng-container>
        <button
          (click)="goToNextPage()"
          [disabled]="currentPage === totalPages"
          class="tw-px-4 tw-py-2 tw-rounded-md tw-text-white hover:tw-bg-blue-600"
        >
          Next
        </button>
      </div>
    </div>
  </section>
</div>
