import { Component, OnInit } from '@angular/core';
import { Book, BookResponse } from 'src/app/core/models/book-response.model';
import { BookService } from 'src/app/core/services/book.service';

@Component({
  selector: 'front-end-internship-assignment-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss'],
})
export class HomeComponent implements OnInit {
  booksList: Book[] = []; // Add booksList property to store the books
  trendingSubjects: Array<any> = [
    { name: 'JavaScript' },
    { name: 'CSS' },
    { name: 'HTML' },
    { name: 'Harry Potter' },
    { name: 'Crypto' },
  ];

  constructor(private bookService: BookService) {}

  ngOnInit(): void {
    // Fetch all books from the BookService
    this.bookService.getAllBooks().subscribe((response: BookResponse) => {
      this.booksList = response.works;
    });
  }
}
