import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, ParamMap } from '@angular/router';
import { SubjectsService } from '../../core/services/subjects.service';
import { SearchService } from '../../core/services/search.service'; // Import the SearchService
import { Book } from 'src/app/core/models/book-response.model';

@Component({
  selector: 'front-end-internship-assignment-trending-subjects',
  templateUrl: './trending-subjects.component.html',
  styleUrls: ['./trending-subjects.component.scss'],
})
export class TrendingSubjectsComponent implements OnInit {

  isLoading: boolean = true;

  subjectName: string = '';

  allBooks: Book[] = [];

  constructor(
    private route: ActivatedRoute,
    private subjectsService: SubjectsService,
    private SearchService : SearchService
  ) {}

  getAllBooks() {
    this.subjectsService.getAllBooks(this.subjectName).subscribe((data) => {
      this.allBooks = data?.works;
      // this.subjectsArray = data;
      this.isLoading = false;
    });
  }

  searchBooks(query: string, limit: number) {
    this.SearchService.searchBooks(query, limit).subscribe((data) => {
      
      // this.allBooks = data?.works;
      // this.isLoading = false;
    });
  }

  ngOnInit(): void {
    this.route.paramMap.subscribe((params: ParamMap) => {
      this.subjectName = params.get('name') || '';
      this.isLoading = true;
      this.getAllBooks();
    });
  }
}
<div *ngIf="allBooks.length > 0">
  <front-end-internship-assignment-table-view
  [booksList]="allBooks"
  [subjectName]="subjectName"
  ></front-end-internship-assignment-table-view>
</div>
