import { Injectable } from '@angular/core';
import { ApiService } from './api.service';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class SearchService {
  constructor(private apiService: ApiService) {}
  // searchBooks(query: string, limit: number): Observable<any> {
  // return this.apiService.get("/search.json?q=null");
  //   // return this.apiService.get(`/search.json?q=${query}&limit=${limit}`);
  //   // console.log(this.apiService.get("/search.json?q=null&limit=10"));
  // }

  // searchBooks(query: string, limit: number, page: number): Observable<any> {
  //   return this.apiService.get(`/search.json?q=${query}&limit=${limit}&page=${page}`);
  // }

  searchBooks(query: string, limit: number, page: number): Observable<any> {
    // Calculate offset based on current page and page size
    const offset = (page - 1) * this.pageSize;
  
    // Modify the API endpoint to include the offset parameter
    return this.apiService.get(`/search.json?q=${query}&limit=${limit}&offset=${offset}`);
  }
  
}
