import { Component, OnInit } from '@angular/core';
import { FormControl } from '@angular/forms';
import { debounceTime, filter } from 'rxjs';
import { SearchService } from '../../core/services/search.service';
import { SearchBook } from 'src/app/core/models/search-book-response.model';

@Component({
  selector: 'front-end-internship-assignment-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss'],
})
export class HomeComponent implements OnInit {
  bookSearch: FormControl;
  searchResultBooks: SearchBook[] = [];
  currentPage: number = 1;
  pageSize: number = 10;
  totalItems: number = 0;

  constructor(private searchService: SearchService) {
    this.bookSearch = new FormControl('');
  }

  trendingSubjects: Array<any> = [
    { name: 'JavaScript' },
    { name: 'CSS' },
    { name: 'HTML' },
    { name: 'Harry Potter' },
    { name: 'Crypto' },
  ];

  ngOnInit(): void {
    this.bookSearch.valueChanges.pipe(debounceTime(300)).subscribe((value: string) => {
      // Update search query and reset pagination to first page
      this.currentPage = 1;
      this.searchBooks(value, this.pageSize, this.currentPage);
    });
    this.searchBooks('', this.pageSize, this.currentPage);
  }

  searchBooks(query: string, limit: number, page: number) {
    const offset = (page - 1) * limit; // Calculate offset based on current page
    this.searchService.searchBooks(query, limit, offset).subscribe((data) => {
      console.log('DATA');
      console.log(data);
      this.searchResultBooks = data?.docs;
      this.totalItems = data?.numFound;
    });
  }

  goToPage(page: number) {
    this.currentPage = page;
    this.searchBooks(this.bookSearch.value, this.pageSize, this.currentPage);
  }

  goToPreviousPage() {
    if (this.currentPage > 1) {
      this.currentPage--;
      this.searchBooks(this.bookSearch.value, this.pageSize, this.currentPage);
    }
  }

  goToNextPage() {
    const totalPages = Math.ceil(this.totalItems / this.pageSize);
    if (this.currentPage < totalPages) {
      this.currentPage++;
      this.searchBooks(this.bookSearch.value, this.pageSize, this.currentPage);
    }
  }
}
<div class="header">
  <div class="tw-text-24-px tw-font-600" style="color: #34b27a;">Open Books Library</div>
  <div class="search-button">
    <mat-icon class="tw-mt-16-px" aria-label="Search Icon">search</mat-icon>
    <input
      [formControl]="bookSearch"
      name="searchKey"
      type="text"
      placeholder="Search Books"
      class="tw-border-0 tw-bg-inherit"
    >
  </div>
</div>
<div class="tw-flex tw-flex-row tw-mt-24-px tw-ml-24-px">
  <section class="trending-subjects">
    <div class="tw-font-600 tw-text-24-px">Trending Subjects</div>
    <ul class="tw-list-none tw-pl-0">
      <li class="tw-text-16-px tw-cursor-pointer tw-p-12-px" *ngFor="let subject of trendingSubjects" routerLink="/trending-subject/{{ subject.name }}">
        {{ subject.name }}
      </li>
    </ul>
  </section>
  <section class="search-results tw-ml-24-px">
    <div class="tw-font-600 tw-text-24-px">Search Results</div>
    <table class="tw-table tw-table-fixed tw-w-full tw-mt-16-px tw-border-collapse">
      <thead>
        <tr>
          <th class="tw-text-16-px tw-font-600 tw-p-12-px tw-border tw-border-gray-500">Title</th>
          <th class="tw-text-16-px tw-font-600 tw-p-12-px tw-border tw-border-gray-500">Author</th>
          <th class="tw-text-16-px tw-font-600 tw-p-12-px tw-border tw-border-gray-500">Year of Publication</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let book of searchResultBooks; let even = even" [ngClass]="{'tw-bg-gray-100': even}">
          <td class="tw-text-16-px tw-p-12-px tw-border tw-border-gray-500">
            <div style="color: #34b27a;">{{ book.title }}</div>
          </td>
          <td class="tw-text-16-px tw-p-12-px tw-border tw-border-gray-500">{{ book.author_name }}</td>
          <td class="tw-text-16-px tw-p-12-px tw-border tw-border-gray-500">{{ book.publish_year }}</td>
        </tr>
      </tbody>
    </table>
    <div class="pagination-container tw-mt-16-px tw-flex tw-justify-center">
      <button (click)="goToPreviousPage()" [disabled]="currentPage === 1">Previous</button>
      <ng-container *ngFor="let page of pagesArray">
        <button (click)="goToPage(page)">{{ page }}</button>
      </ng-container>
      <button (click)="goToNextPage()" [disabled]="currentPage === totalPages">Next</button>
    </div>
    
  </section>
</div>
